import "./App.css";
import "./components/layout/container.css";
import { LotteryButton } from "./atoms/Button.jsx";
import { Text } from "./atoms/text/Text";
import { Header } from "./components/layout/container.jsx";
import { Footer } from "./components/layout/container.jsx";
import { useEffect, useState } from "react";

function Lottery() {
  const [number, setNumber] = useState();
  const pickANumber = () => Math.round(Math.random() * 91);

  const LotteryButtonClick = () => {
    const newNumber = pickANumber();
    setNumber(newNumber);
    console.log(`hai cliccato sul buttone, numero estratto ${newNumber}!`);
  };
  return (
    <div>
      <h1>Lotteria</h1>
      <p>Numero estratto : `{number}` </p>
      <LotteryButton wasDraw={false} clickHandler={LotteryButtonClick} />
    </div>
  );
}

const API_ENDPOINT = {
  BASE: `https://random-data-api.com/api/v2`,

  get BEERS() {
    return `${this.BASE}/beers`;
  },

  CREDIT_CARDS: `credit_cards`,
};

export default function App() {
  const url = `${API_ENDPOINT.BASE}/${API_ENDPOINT.BEERS}`;
  console.log(url);

  const [randomBeer, setRandomBeer] = useState(null);

  useEffect(() => {
    console.log("Load");
    fetch(API_ENDPOINT.BEERS)
      .then((r) => r.json)
      .then((beer) => {
        console.log({ beer });
        setRandomBeer(beer);
      })
      .catch(console.error);

    return () => {
      console.log("unmount");
    };
  }, []);

  const data = {
    id: 4986,
    uid: "de2bd275-a201-408c-a69e-dfb9d69a6087",
    brand: "Hoegaarden",
    name: "Delirium Noctorum",
    style: "Pilsner",
    hop: "Sterling",
    yeast: "1099 - Whitbread Ale",
    malts: "Special roast",
    ibu: "71 IBU",
    alcohol: "7.2%",
    blg: "7.4°Blg",
  };

  const { id, brand, name, style, malt, alcohol } = randomBeer;

  return (
    <div className="App">
      {Header({ Header })}
      <section className="beer">
        <Text as="h1" variant="title">
          Estrazione casuale birra da provare!
        </Text>
        <Text as="h3" variant="title">
          Clicca sul bottone!
        </Text>
        <div>Beer code: {id}</div>
        <h3>{brand}</h3>
        <h3>{name}</h3>
        <h3>{style}</h3>
        <h3>{malt}</h3>
        <h3>{alcohol}</h3>
        {JSON.stringify(randomBeer)}
      </section>
      <section className="number">
        <Text as="h1" variant="title">
          Estrazione casuale di un numero!
        </Text>
        <Text as="h3" variant="title">
          Clicca sul bottone!
        </Text>
      </section>
      <section className="body">
        <Lottery />
      </section>
      <section className="footer">{Footer({ Footer })}</section>
    </div>
  );
}
