import "./App.css";
import "./components/layout/container.css";
import { LotteryButton } from "./atoms/Button.jsx";
import { Text } from "./atoms/text/Text";
import { Header } from "./components/layout/container.jsx";
import { Footer } from "./components/layout/container.jsx";
import { useState } from "react";

function Lottery() {
  const [number, setNumber] = useState();
  const pickANumber = () => Math.round(Math.random() * 91);

  const LotteryButtonClick = () => {
    const newNumber = pickANumber();
    setNumber(newNumber);
  };
  return (
    <div>
      <h1>Lotteria</h1>
      <p>Numero estratto : </p>
      <LotteryButton clickHandler={LotteryButtonClick} />
    </div>
  );
}

export default function App() {
  /*  const button = {
    label: "Show",
    title: "title3",
  }; */

  return (
    <div className="App">
      {Header({ Header })}
      <Text as="h1" variant="title">
        Ciao! Clicca sul bottone ed estrai un numero!
      </Text>
      <Lottery />
      <section className="footer">
        {/*   {Button({ button })} */}
        {Footer({ Footer })}
      </section>
    </div>
  );
}
